import {
	InputGroup,
	InputLeftAddon,
	Icon,
	Input,
	FormControl,
	FormHelperText,
	Stack,
	Button,
} from '@chakra-ui/react';

import { EmailIcon, LockIcon } from '@chakra-ui/icons'

const LoginForm = () => {
	return (
		<form action="submit">
		<FormControl textAlign='center'>
			<Stack spacing={3} m={3}>
				<InputGroup>
					<InputLeftAddon children={<EmailIcon />} />
					<Input
						type='email'
						placeholder='Email'
						aria-label='Email input field'
					/>
				</InputGroup>
				<InputGroup>
					<InputLeftAddon children={<LockIcon />} />
					<Input
						type='password'
						placeholder='Password'
						aria-label='Password input field'
					/>
				</InputGroup>
				<Button boxShadow='md' _active={{ boxShadow: 'lg' }} type="submit">
					Login
				</Button>
				<FormHelperText id='email-helper-text' textAlign='center'>
					Welcome back!
					<br />
					üè†
				</FormHelperText>
			</Stack>
		</FormControl>
		</form>
	);
};

export default LoginForm;
