import {
	Text,
	Flex,
	Tabs,
	TabList,
	Tab,
	TabPanels,
	TabPanel,
} from '@chakra-ui/react';
import SingupForm from '../components/SignupForm';
import LoginForm from '../components/LoginForm';

const Form = () => {
	return (
		<Flex
			direction='column'
			p={3}
			boxShadow='sm'
			// w='350px'
			bg="gray.100"
			rounded='lg'
			justify='center'
			align='center'>
			<Text fontSize='lg' py={10}>Sign up or login to your account.</Text>
			<Tabs isFitted variant='enclosed-colored' m={2} w='100%'>
				<TabList>
					<Tab>Sign up</Tab>
					<Tab>Login</Tab>
				</TabList>
				<TabPanels>
					<TabPanel>
						<SingupForm />
					</TabPanel>
					<TabPanel>
						<LoginForm />
					</TabPanel>
				</TabPanels>
			</Tabs>
		</Flex>
	);
};

export default Form;
