import { useState } from "react";
import {
	InputGroup,
	InputLeftAddon,
	Input,
	FormControl,
	Stack,
	Button,
	Divider
} from '@chakra-ui/react';
import { EmailIcon, LockIcon, InfoIcon } from '@chakra-ui/icons'

const LoginForm = (props) => {
	const [formValues, setFormValues] = useState({
		fname: "",
		lname: "",
    username: "",
    password: "",
  });

  function handleSubmit(event) {
    event.preventDefault();
  }

	return (
		<form onSubmit={handleSubmit}>
			<Stack spacing={3}>
			<FormControl isRequired>
				<InputGroup>
					<InputLeftAddon children={<LockIcon />} />
					<Input
						bg="gray.50"
						aria-lable='First name input field'
						id='fname'
						type='name'
						placeholder='First Name:'
						value={formValues.fname}
          	name="fname"
          	onChange={(event) => {
							setFormValues({
								...formValues,
								[event.target.name]: event.target.value,
							})
          	}}
					/>
				</InputGroup>
			</FormControl>
			<FormControl isRequired>
				<InputGroup>
					<InputLeftAddon children={<LockIcon />} />
					<Input
						bg="gray.50"
						id='lname'
						type='name'
						placeholder='Last Name:'
						aria-lable='Last name input field'
						value={formValues.lname}
          	name="lname"
          	onChange={(event) => {
							setFormValues({
								...formValues,
								[event.target.name]: event.target.value,
							})
          	}}
					/>
				</InputGroup>
			</FormControl>
			<Divider borderColor='gray.300' />
			<FormControl isRequired>
				<InputGroup>
					<InputLeftAddon children={<EmailIcon />} />
					<Input
						bg="gray.50"
						id='email'
						type='email'
						placeholder='Email:'
						aria-lable='Email input field'
						value={formValues.email}
          	name="email"
          	onChange={(event) => {
							setFormValues({
								...formValues,
								[event.target.name]: event.target.value,
							})
          	}}
					/>
				</InputGroup>
			</FormControl>
			<FormControl isRequired>
				<InputGroup>
					<InputLeftAddon children={<InfoIcon />} />
					<Input
						bg="gray.50"
						id='password'
						type='password'
						placeholder='Password:'
						aria-lable='Password input field'
						value={formValues.password}
          	name="password"
          	onChange={(event) => {
							setFormValues({
								...formValues,
								[event.target.name]: event.target.value,
							})
          	}}
					/>
				</InputGroup>
			</FormControl>
			<Button boxShadow='md' _active={{ boxShadow: 'lg' }} type="submit" bg="gray.200">
					Sign Up
			</Button>
			</Stack>
		</form>
	);
};

export default LoginForm;
